import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


interface SquarePropsInterface {
    value: String;
    onClick: () => void
}

function Square(props: SquarePropsInterface) {
    return (
        <button className="square" onClick={() => props.onClick()}>
            {props.value}
        </button>
    );
 }

interface BoardStateInterface {
    squares: Array<string>;
    onClick: () => void
}

interface BoardPropsInterface {
    squares: string;
    xIsNext: boolean;
    onClick: () => void
}

 class Board extends React.Component<BoardStateInterface, BoardPropsInterface> {
    renderSquare(i: number) {
        return (
            <Square 
                value={this.state.squares[i]}
                onClick={() => this.props.onClick(i)} 
                />
        );
    }

    render() {
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
 }

interface GameStateInterface {
    // history: [{
    //     squares: Array<string>;
    // }];
}

interface GamePropsInterface {

    xIsNext: boolean;
}

interface GamePropsInterface2 {
    history: [{
        squares: Array<string>;
    }];

}

 class Game extends React.Component<GameStateInterface, GamePropsInterface2, GamePropsInterface>{
    constructor(props: GamePropsInterface) {
        super(props);
        this.state = {
          history: [{
            squares: Array(9).fill(null),
          }],
          xIsNext: true,
        };
    }
    handleClick(i) {
        const history = this.state.history;
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (calculateWinner(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            history: history.concat([{
                squares: squares,
              }]),
            xIsNext: !this.state.xIsNext
        });
    }
    render() {
        const history = this.state.history;
        const current = history[history.length - 1];
        const winner = calculateWinner(current.squares);
        let status;
        if (winner) {
          status = 'Winner: ' + winner;
        } else {
          status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

        return (
            <div className="game">
                <div className="game-board">
                <Board
                    squares={current.squares}
                    onClick={(i) => this.handleClick(i)}
                />
                </div>
                <div className="game-info">
                    <div>{status}</div>
                </div>
            </div>
        );
    }
 }

 // ========================================

 ReactDOM.render(
    <Game />,
    document.getElementById('root')
 );

 function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }
